mapper.select:
SELECT i.id chapter_id , i.product_id, i.parent_id, i.resource_type , i.`name` chapter_name ,i.chapter_id chapter_index, IF(ISNULL(r.star_level),0,r.star_level) star_level, IF(ISNULL(r.score),0,r.score) score, i.top_user, i.complete_count, i.picture_url, IF(ISNULL(r.status),0,r.status) status FROM xd_course_product_item i LEFT JOIN xd_user_course_chapter_record r ON i.product_id=r.course_id AND i.id = r.item_id AND r.user_id = #{userId}
and i.resource_type = 1
and i.product_id= #{courseId}
and i.id= #{chapterId}
order by list_order asc;

SELECT i.id chapter_id, i.product_id, i.parent_id, i.resource_type , i.`name` chapter_name ,i.chapter_id chapter_index, IF(ISNULL(r.star_level),0,r.star_level) star_level, IF(ISNULL(r.score),0,r.score) score, i.top_user, i.complete_count, i.picture_url, IF(ISNULL(r.status),0,r.status) status FROM xd_course_product_item i LEFT JOIN xd_user_course_chapter_record r ON i.product_id=r.course_id AND i.id = r.item_id AND r.user_id = #{userId}
and i.resource_type = 1
and i.product_id= #{courseId}
and i.parent_id= #{chapterId}
order by list_order asc;
